import React, { useState } from 'react';

import Expenses from './Components/Expenses/Expenses';
import NewExpense from './Components/NewExpense/NewExpense';

const INITIAL_EXPENSES = [
  { id: "e1", title: 'BEER', amount: 2000, date: new Date(2021, 5, 28) },
  {id: "e1",  title: 'MILK', amount: 1999, date: new Date(2021, 5, 27) },
  { title: 'SODA', amount: 1998, date: new Date(2021, 5, 26) },
  { title: 'JUICE', amount: 1997, date: new Date(2021, 5, 25) },
];


function App(amount, title, date) {

  const [expenses, setExpenses] = useState(INITIAL_EXPENSES)

  const addExpenseHandler = expense => {
    setExpenses(prevExpenses => {
      return [expense, ...prevExpenses]
    });
  }

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={expenses}/>
    </div>
  );
}

export default App;
